#!/usr/bin/env python3
# -*- encoding: UTF8 -*-

import unittest

from dnsuptools import dkimrecgen

class TestDKIMrecgen(unittest.TestCase):
    def testDKIMFromFile1024(self):
        dkim = dkimrecgen.dkimFromFile({'filename': 'tests/testdkimkey1024.txt'})
        self.assertEqual(dkim, {'filename': 'tests/testdkimkey1024.txt', 'p': 'MIGfMA0GCSqGSIb3DQEBAQUAA4GNADCBiQKBgQDQP7L2UVYrwo6pNHEXzSGrT7B+P8HBTEX6AecR9xTHr+Y9WwiYJAaKlLESqTB5SzQYlO6c+QeOv1fhnExBRxMqQEM0FhpvdwBadYx5Df4bSxt/5fjherBAiE9FbnTWWNDO6isGdAJ75+yWceRvXx0MajqSUwggqaZWNirWJUq34QIDAQAB', 'keyname': 'testdkimkey', 'v': 'DKIM1', 'k': 'rsa'})

    def testDKIMFromFile2048(self):
        dkim = dkimrecgen.dkimFromFile({'filename': 'tests/testdkimkey2048.txt'})
        self.assertEqual(dkim, {'filename': 'tests/testdkimkey2048.txt', 'p': '', 'keyname': 'testdkimkey', 'v': 'DKIM1', 'k': 'rsa'})

    def testDKIMFromFile4096(self):
        dkim = dkimrecgen.dkimFromFile({'filename': 'tests/testdkimkey4096.txt'})
        self.assertEqual(dkim, {'filename': 'tests/testdkimkey4096.txt', 'p': '', 'keyname': 'testdkimkey', 'v': 'DKIM1', 'k': 'rsa'})


